    // call the "approve" function
    async public void Approve() {
    


        if (tokenID >= 128)
        {
            maxSupply = maxLegendarySupply;
            cost = Legendary_items_cost;
        }
            
        else if (tokenID >= 99)
        {   
            maxSupply = maxEpicSupply;
            cost = Epic_items_cost;
        }
           
        else if (tokenID >= 64)
        {
            maxSupply = maxRareSupply;
            cost = Rare_items_cost;
        }
            
        else if (tokenID >= 37)
        {
            maxSupply = maxUncommonSupply;
            cost = Uncommon_items_cost;
        }
        
        else if (tokenID >= 10)
        {
            maxSupply = maxCommonSupply;
            cost = Common_items_cost;
        }
        
        else 
        {
            maxSupply = maxLeaderSupply;
            cost = Leader_items_cost;
        }



        // spender
        string spender = PlayerPrefs.GetString("Account");
       // function name
        string method = "approve";

        amount = cost.ToString()+"000000000000000000";
        Debug.Log(amount);
       // put arguments in an array of string
        string[] obj = {spender, amount};
       // serialize arguments
        string args = JsonConvert.SerializeObject(obj);

        string data = await EVM.CreateContractData(DexCoin_abi, method, args);
        Debug.Log(data);
        Debug.Log(spender);
        string value = "0";
       // gas limit OPTIONAL
        string gasLimit = "";
       // gas price OPTIONAL
        string gasPrice = "";
        try {
            string response = await Web3Wallet.SendTransaction(chainId, DexCoin_contract, value, data, gasLimit, gasPrice);           
            Debug.Log(response);
        }catch(Exception e) {
            Debug.LogException(e, this);
        }

    }

