        // gas price: REQUIRED
        string gasPrice = "";
        try{
            string response = await Web3Wallet.SendTransaction(chainId, DexNFT_contract, value, data, gasLimit, gasPrice);           
            Debug.Log(response);
        }catch(Exception e) {
            Debug.LogException(e, this);
        }

    }

    
